INSERT INTO "Book" ("BookId", "Title", "Description", "Isbn13", "Inventory", "Price", "DiscountPercent", "NumberOfPage", "PublicationDate", "ImageURL", "authorId", "publisherID", "CreatedTime", "CreatedBy", "ModifiedTime", "ModifiedBy") VALUES ('17bb394b-19b7-4537-ba32-56bfb15e2727', 'Chí Phèo', 'Chí Phèo là một truyện ngắn nổi tiếng của nhà văn Nam Cao viết vào tháng 2 năm 1941. Chí Phèo là một tác phẩm xuất sắc, thể hiện nghệ thuật viết truyện độc đáo của Nam Cao, đồng thời là một tấn bi kịch của một người nông dân nghèo bị tha hóa trong xã hội. Chí Phèo cũng là tên nhân vật chính của truyện.', '12345678910', 200, '$200,000.00', '0', 200, '2025-01-01 00:00:00+07', 'https://localhost:7287/uploads/8feae403-d246-4e4e-b05c-fef6fb773497.jpg', '4e366e12-6f13-4da5-b3bb-0574c3770463', '217ec3b7-e512-4077-876b-9d9fee217c79', '2025-06-11 15:28:20.524213+07', 'ADMIN', NULL, NULL);
INSERT INTO "Book" ("BookId", "Title", "Description", "Isbn13", "Inventory", "Price", "DiscountPercent", "NumberOfPage", "PublicationDate", "ImageURL", "authorId", "publisherID", "CreatedTime", "CreatedBy", "ModifiedTime", "ModifiedBy") VALUES ('4b40db56-7cab-434c-b561-362e0855a9a6', 'Sổ đỏ', 'Số đỏ là một tiểu thuyết văn học của nhà văn Vũ Trọng Phụng (ông vua phóng sự đất Bắc) đăng trên Hà Nội báo từ số 40 ngày 7 tháng 10 năm 1936 và được in thành sách lần đầu vào năm 1938, với 20 chương. Tác phẩm xoay quanh nhân vật chính là Xuân - biệt danh là Xuân Tóc đỏ - từ chỗ là một kẻ bị coi là hạ lưu, bỗng chốc đã bước lên tầng lớp danh giá của xã hội nhờ trào lưu Âu hóa của giới tiểu tư sản Hà Nội khi đó. Tác phẩm Số đỏ, cũng như các tác phẩm khác của Vũ Trọng Phụng đã từng bị cấm lưu hành tại Việt Nam Dân chủ Cộng hòa trước năm 1975 cũng như tại Việt Nam sau ngày thống nhất từ năm 1975 cho đến năm 1986.[1] Cho đến nay, Số đỏ đã được tái xuất bản và được phê duyệt ở Việt Nam. Đồng thời một đoạn trích của tác phẩm này cũng được đưa vào chương trình Ngữ văn lớp 12 (sách giáo khoa Ngữ Văn 12, chương trình mới, tập 1) với tên gọi: Xuân Tóc Đỏ cứu quốc (Nhan đề văn bản là một phần tên của chương XX trong tiểu thuyết Số đỏ: Xuân Tóc Đỏ cứu quốc - Xuân Tóc Đỏ vĩ nhân - Nỗi buồn của ông bố vợ không bị đấm.)', '12345678910', 155, '$300,000.00', '0.15', 200, '2025-06-17 00:00:00+07', 'https://localhost:7287/uploads/3a4c9e55-febf-4507-b82e-a462a2ab4971.jpg', 'e7094b37-8284-465d-aed4-70a539274b10', '217ec3b7-e512-4077-876b-9d9fee217c79', '2025-06-11 15:30:22.294002+07', 'ADMIN', NULL, NULL);
